import axios from "axios"
import { useState, useEffect } from "react";
import classes from "../component/properties/products/PropertyItem.module.css"
import {AgGridColumn, AgGridReact} from 'ag-grid-react';

import 'ag-grid-community/dist/styles/ag-grid.css';
import 'ag-grid-community/dist/styles/ag-theme-alpine.css';




function SupplyOrderPage() {

    var rowData = [
        {make: "Toyota", model: "Celica", price: 35000},
        {make: "Ford", model: "Mondeo", price: 32000},
        {make: "Porsche", model: "Boxter", price: 72000}
    ];


    const [isLoading, setIsLoading] = useState(true);
    const [loadedProp, setLoadedProp] = useState([]); // all locations
    function generatorHandler(){

    }


    function makeTable(locations){
        var data =[];
        var i = 0;
        locations.Location.forEach(location => {
           if(location.productBarcode.length>3){
            axios.get("http://localhost:3000/products/"+ location.productBarcode)
            .then((response) => {
             //console.log(response.data.product[0])
             console.log(i)
             data[i] = {make:response.data.product[0].barcode, model: response.data.product[0].name, price: response.data.product[0].price }
             i++;
            })
            .then(() => {
              setIsLoading(false)
            });               
           } 
        });
        // var rowData = [
        //     {make: "Toyota", model: "Celica", price: 35000},
        //     {make: "Ford", model: "Mondeo", price: 32000},
        //     {make: "Porsche", model: "Boxter", price: 72000}
        // ];
        // rowData[3] = {make:"toyota", model: "supra", price: 2313}
        return rowData;
       
    }


    useEffect(() => {       
        setIsLoading(true);
        axios.get("http://localhost:3000/locations/")
        .then((response) => {
          setLoadedProp(response.data)
          //makeTable(response.data)
          return response.data.product
        })
        .then(() => {
          setIsLoading(false)
        });
    
      }, []);

      if(isLoading){
        return (
          <section>
            <p>
              loading...
            </p>
          </section>
        );
      }
      return (
        <section>
            <button onClick={generatorHandler}>Generate supply order</button>
          <div className="ag-theme-alpine" style={{height: 400, width: 600}}>
           <AgGridReact
               rowData={makeTable(loadedProp)}>
               <AgGridColumn field="make"></AgGridColumn>
               <AgGridColumn field="model"></AgGridColumn>
               <AgGridColumn field="price"></AgGridColumn>
           </AgGridReact>
       </div>
        </section>
      );

}

export default SupplyOrderPage;
