import classes from "./PropertyItem.module.css";
import { useState, useEffect } from "react";
import axios from "axios";

function PropertyDetailedItem(props) {
  const [location, setLocation] = useState(""); //location searcbar
  const [number, setNumber] = useState({}); //added number
  const [loadedLoc, setloadedLoc] = useState([]); //loaded location
  const [loadedProduct, setLoadedProduct] = useState({}); //loaded product
  const [isLoading, setIsLoading] = useState(true);
  useEffect(() => {
    setIsLoading(true);
    axios.get("http://localhost:3000/products/"+props.barcode)
    .then((response) => {
      setLoadedProduct(response.data)
      return response.data.product
    })
    .then(() => {
      setIsLoading(false)
    });

  }, []);
  function submitHandler() {
    console.log(location);
    axios
      .get("http://localhost:3000/locations/" + location)
      .then((response) => {
        setloadedLoc(response.data);
      });
    if(location === props.locationBarcode){
      var N1 = parseInt(number)
      var N2 = parseInt(loadedLoc.docs[0].productNumber)
      var N3 = parseInt(N1+N2)
      console.log(N3)

      const update = {
        format: "json",
          "update": [{
            "propName":"productNumber", "value": N3
          }]
      };
      console.log(update)
      updatePostLocation(update, location);
    }else{
      const update3 = {
        format: "json",
        "update": [{
            "propName":"productNumber", "value": 0,
          },{
            "propName": "productBarcode", "value": "",
          }]
        };
      updatePostLocation(update3, props.locationBarcode);
      const update = {
        format: "json",
          "update": [{
            "propName":"productNumber", "value": number,
          },{
            "propName": "productBarcode", "value": props.barcode,
          }]
      };
    
      updatePostLocation(update, location);
      const update2 = { 
        format: "json",
        "update": [{
          "propName":"locationBarcode", "value": location,
        }],
      };
      updatePostProduct(update2, props.barcode);
      reloadProduct()
    }
    function reloadProduct(){}
  }

  return (
    <div>
      <div className={classes.backgroundimage}>
        <img
          src={props.image}
          onError={({ currentTarget }) => {
            currentTarget.onerror = null; // prevents looping
            currentTarget.src =
              "https://us.123rf.com/450wm/infadel/infadel1712/infadel171200119/91684826-een-zwart-lineair-fotocamera-logo-zoals-geen-afbeelding-beschikbaar-.jpg?ver=6";
          }}
          alt={props.title}
        />
      </div>
      <div className={classes.content}>
        <h3>{props.title}</h3>
        <p>price: {props.price}</p>
        <p>barcode: {props.barcode}</p>
        <p>
          location:{" "}
          {props.locationBarcode ? props.locationBarcode : "not in store"}{" "}
        </p>
        <p>minimal in store: {props.min} </p>
      </div>
    </div>
  );
}

export default PropertyDetailedItem;
