import classes from "./PropertyList.module.css";
import PropertyItem from "./PropertyItem";
import {useState} from 'react'
function PropertyList(props) {
  const [productBarcode, setProduct] = useState('')
  const [location, setLocation] = useState('')
  function submitHandler(){
    console.log()
  }
  console.log(props.products.product)
  return (
    <div>
      <input type="text" placeholder = "product barcode" onChange={event => {setProduct(event.target.value)}}/>
      <input type="text" placeholder = "location barcode"/>
      <button onClick={submitHandler}>add product to location</button>
    <ul className={classes.list}>
      {
      props.products.product.filter((product)=>{
        if(productBarcode = ""){
          return product
        }else if(product.barcode.includes(productBarcode)){
          return product
        }
      }).map((product) => (
        <PropertyItem
          key={product.id}
          barcode={product.barcode}
          title={product.name}
          price={product.price}
          image={product.image}
          locationBarcode={product.locationBarcode}
        />
      ))}
    </ul>
    </div>
  );
}

export default PropertyList;
